import React, {Fragment, useState} from 'react';
// eslint-disable-next-line no-unused-vars
import axios from 'axios';
import './ContactForm.css';
import { Button } from './Button';

const ContactForm = () => {

    const [datos, setDatos] = useState({
        name: '',
        email: '',
        message: '',
    })

    const handleInputChange = (event) => {
        // console.log(event.target.name)
        // console.log(event.target.value)
        setDatos({
            ...datos,
            [event.target.name] : event.target.value
        })
    }

    const enviarDatos = (event) => {
        event.preventDefault()
        console.log('enviando datos...' + datos.name + ' '  + datos.email  + datos.message)
    }

    return (
        <Fragment>
            <div className='contact-container'>
            <h1 className='contact-heading'>Contact Us</h1>
              <p className='contact-form-text'>
                Weâ€™re available for remote projects around the world.
                 </p>
            <form className="form__container-card" onSubmit={enviarDatos}>
                <div className="">
                    <input type="text"
                     placeholder="Name" className="form-input" 
                     onChange={handleInputChange} name="name">
                     </input>
                </div>
                <div className="">
                    <input type="text"
                     placeholder="Email" className="form-input" 
                     onChange={handleInputChange} name="email">
                     </input>
                </div>
                <div className="">
                    <textarea type="text"
                     placeholder="Message" className="form-input" 
                     onChange={handleInputChange} name="message">
                     </textarea>
                </div>
                <Button type="submit"  className='btn--primary'  buttonSize='btn--wide' buttonColor='pink'>
                    Send
                    </Button>
            </form>
            </div>
        </Fragment>
    );
}     


export default ContactForm;
