import React, {Fragment, useState} from 'react';
// eslint-disable-next-line no-unused-vars
import axios from 'axios';
import './ContactForm.css';
import { Button } from './Button';
import { useForm } from 'react-hook-form'


const ContactForm = () => {

    const [data, setData] = useState({
        name: '',
        email: '',
        subject: '',
        message: '',
    })

    const handleInputChange = (event) => {
        // console.log(event.target.name)
        // console.log(event.target.value)
        setData({
            ...data,
            [event.target.name] : event.target.value
        })
    }

    const sendData = (event) => {
        event.preventDefault()
        console.log('sending data...' + datos.name + ' ' + datos.email  + datos.subject  + datos.message)
    }


    return (
        <Fragment>
            <div className='contact-container'>
            <h1 className='contact-heading'>Contact Us</h1>
              <p className='contact-form-text'>
                We’re available for remote projects around the world.
                 </p>
            <form onSubmit={sendData} className="form__container-card">
                <div className="">
                    <input type="text" name="name"
                     placeholder="Name" className="form-input" 
                     ref={register({
                        required: {
                            value: true, 
                            message: 'Name is required'
                            }, 
                        maxLength: {
                            value: 5, 
                            message: 'No more tan 10 characters!'
                            },
                        minLength: {
                            value: 2, 
                            message: 'Minimum 2 characters'
                            }
                    })}
                     >
                     </input>

                </div>
                <div className="">
                <input type="email" name="email"
                     placeholder="Email address" className="form-input" 
                     ref={register({
                        required: {
                            value: true, 
                            message: 'Email is required'
                            }, 
                    })}
                     >
                     </input>

                </div>
                <div className="">
                     <input type="text" name="subject"
                     placeholder="Subject" className="form-input" 
                     ref={register({
                     required: {
                        value: true, 
                        message: 'Please field out this field'
                        }, 
                    minLength: {
                        value: 2, 
                        message: 'Please field out this field'
                        }
                })}
                     >
                     </input>

                </div>
                <div className="">
                    <textarea type="text" name="message"
                     placeholder="Message" className="form-input" 
                     ref={register({
                        required: {
                           value: true, 
                           message: 'Please field out this field'
                           }, 
                       minLength: {
                           value: 5, 
                           message: 'Please field out this field'
                           }
                   })}
                     >
                     </textarea>

                </div>
                <Button type="submit"  className='btn--primary'  buttonSize='btn--wide' buttonColor='pink'>
                    Send
                    </Button>
            </form>
            </div>
        </Fragment>
    );
}     


export default ContactForm;
