import React, {Fragment, useState} from 'react';
// eslint-disable-next-line no-unused-vars
import axios from 'axios';
import './ContactForm.css';
import { Button } from './Button';
import { useForm } from 'react-hook-form'


const ContactForm = () => {

    const {register, errors, handleSubmit} = useForm();

    const [Event, setData] = useState([]);

    const onSubmit = (data, e) => {
        console.log(data);
        setData([
            ...Event,
            data
        ])
        e.target.reset()
    }

    return (
        <Fragment>
            <div className='contact-container'>
            <h1 className='contact-heading'>Contact Us</h1>
              <p className='contact-form-text'>
                Weâ€™re available for remote projects around the world.
                 </p>
            <form onSubmit={handleSubmit(onSubmit)} className="form__container-card">
                <div className="">
                    <input type="text" name="name"
                     placeholder="Name" className="form-input" 
                     ref={register({
                        required: {
                            value: true, 
                            message: 'Name is required'
                            }, 
                        maxLength: {
                            value: 5, 
                            message: 'No more tan 10 characters!'
                            },
                        minLength: {
                            value: 2, 
                            message: 'Minimum 2 characters'
                            }
                    })}
                     >
                     </input>
                     { errors.name && 
                        <span className="">
                          {errors.name.message}
                      </span>}
                </div>
                <div className="">
                <input type="email" name="email"
                     placeholder="Email address" className="form-input" 
                     ref={register({
                        required: {
                            value: true, 
                            message: 'Email is required'
                            }, 
                    })}
                     >
                     </input>
                     { errors.email && 
                        <span className="">
                          {errors.name.email}
                      </span>}
                </div>
                <div className="">
                     <input type="text" name="subject"
                     placeholder="Subject" className="form-input" 
                     ref={register({
                     required: {
                        value: true, 
                        message: 'Please field out this field'
                        }, 
                    minLength: {
                        value: 2, 
                        message: 'Please field out this field'
                        }
                })}
                     >
                     </input>
                     { errors.subject && 
                        <span className="">
                          {errors.name.subject}
                      </span>}
                </div>
                <div className="">
                    <textarea type="text" name="message"
                     placeholder="Message" className="form-input" 
                     ref={register({
                        required: {
                           value: true, 
                           message: 'Please field out this field'
                           }, 
                       minLength: {
                           value: 5, 
                           message: 'Please field out this field'
                           }
                   })}
                     >
                     </textarea>
                     { errors.message && 
                        <span className="">
                          {errors.name.message}
                      </span>}
                </div>
                <Button type="submit"  className='btn--primary'  buttonSize='btn--wide' buttonColor='pink'>
                    Send
                    </Button>
            </form>
            </div>
        </Fragment>
    );
}     


export default ContactForm;
