import React, {Fragment, useState} from 'react';
// eslint-disable-next-line no-unused-vars
import axios from 'axios';
import './ContactForm.css';
import { Button } from './Button';

const ContactForm = () => {

    const [datos, setDatos] = useState({
        name: '',
        email: '',
        message: '',
    })

    const handleInputChange = (event) => {
        // console.log(event.target.name)
        // console.log(event.target.value)
        setDatos({
            ...datos,
            [event.target.name] : event.target.value
        })
    }

    const enviarDatos = (event) => {
        event.preventDefault()
        ('enviando datos...' + datos.name + ' '  + datos.email  + datos.message)
    }

    return (
        <Fragment>
            <h1>Contact Form</h1>
            <form className="contact-container" onSubmit={enviarDatos}>
                <div className="footer-form">
                    <input type="text"
                     placeholder="Name" className="form-input" 
                     onChange={handleInputChange} name="name">
                     </input>
                </div>
                <div className="footer-form">
                    <input type="text"
                     placeholder="Email" className="form-input" 
                     onChange={handleInputChange} name="email">
                     </input>
                </div>
                <div className="footer-form">
                    <textarea type="text"
                     placeholder="Message" className="form-input" 
                     onChange={handleInputChange} name="message">
                     </textarea>
                </div>
                <Button type="submit"  className='btn--wide'  buttonSize='btn--medium' buttonColor='pink'>
                    Send
                    </Button>
            </form>
            <ul>
                <li>{datos.name}</li>
                <li>{datos.email}</li>
                <li>{datos.message}</li>
            </ul>
        </Fragment>
    );
}     


export default ContactForm;
