import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaTimes } from 'react-icons/fa'; 
import { Button }  from '../components/Button';
import './Navbar.css';
import { IconContext } from 'react-icons/lib'
import logoFooter from '../assets/images/logo_catapulta_footer.svg';

function Navbar() {
   const [click, setClick] = useState(false);
   const [button, setButton] = useState(true);

   const handleClick = () => setClick(!click);
   const closeMobileMenu = () => setClick(false);

   const showButton = () => {
       if(window.innerWidth <= 960) {
           setButton (false);
       } else {
           setButton (true);
       }
   };

   useEffect(() => {
      showButton();
   }, []);

   window.addEventListener('resize', showButton);


    return (
        <>
        <IconContext.Provider value={{ color: '#fff'}}>
        <div className="navbar">
            <div className="navbar-container container">
            <Link to='/home'onClick={closeMobileMenu}> <img src={logoFooter} className="logo" alt="logo"/>
                </Link>
                <div className="menu-icon" onClick={handleClick} >
                { click ? <FaTimes /> : <FaBars />}
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                  <li className='nav-item'>
                      <Link to='/home' className='nav-links' onClick={closeMobileMenu}>
                        Home
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/Pricing' className='nav-links' onClick={closeMobileMenu}>

                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/Proyects' className='nav-links' onClick={closeMobileMenu}>

                      </Link>
                  </li>
                  <li className='nav-btn'>
                      {button ? (
                        <Link to='/sign-up' className='btn-link'>
                          <Button buttonStyle='btn--outline'>
                              SIGN UP
                          </Button>
                      </Link>
                      ) : (
                        <Link to='/sign-up' className='btn-link' onClick={closeMobileMenu}>
                        <Button buttonStyle='btn--outline' buttonSize='btn--mobile'>

                        </Button>
                    </Link>  
                      )}
                  </li>
                </ul>
            </div>     
        </div>
    </IconContext.Provider>
  </>
    );
}

export default Navbar;
