import React, {Fragment, useState} from 'react';
// eslint-disable-next-line no-unused-vars
import axios from 'axios';
import './ContactForm.css';
import { Button } from './Button';

const ContactForm = () => {

    const [datos, setDatos] = useState({
        name: '',
        email: ''
    })

    const handleInputChange = (event) => {
        // console.log(event.target.name)
        // console.log(event.target.value)
        setDatos({
            ...datos,
            [event.target.name] : event.target.value
        })
    }

    const enviarDatos = (event) => {
        event.preventDefault()
        console.log('enviando datos...' + datos.name + ' ' + datos.email)
    }

    return (
        <Fragment>
            <h1>Formulario</h1>
            <form className="row" onSubmit={enviarDatos}>
                <div className="">
                    <input type="text"
                     placeholder="Name" className="form-input" 
                     onChange={handleInputChange} name="nombre">
                     </input>
                </div>
                <div className="">
                    <input type="text"
                     placeholder="Email" className="form-input" 
                     onChange={handleInputChange} email="email">
                     </input>
                </div>
                <Button type="submit" buttonSize='btn--medium' buttonColor='pink'>
                    Send
                    </Button>
            </form>
            <form>
            <input
              className='footer-input'
              name='email'
              type='email'
              placeholder='Your Email'
            />
            <Button buttonStyle='btn--outline'>Send</Button>
          </form>
            <ul>
                <li>{datos.name}</li>
                <li>{datos.email}</li>
            </ul>
        </Fragment>
    );
}     


export default ContactForm;
